import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {
  generateAndStoreTransitKey,
  makeAuthRequest,
  redirectToSignInWithAuthRequest,
  DEFAULT_SCOPE,
  isUserSignedIn,
  isSignInPending,
  handlePendingSignIn,
  redirectToSignIn
} from 'blockstack';

import isElectron from 'is-electron';

import { Plugins } from '@capacitor/core';

import AnalyticsService from '../services/AnalyticsService';
import PresentingService from '../services/PresentingService';


export default class Signin extends Component {

  static propTypes = {
    history: PropTypes.any
  };

  state = {
    loaded: false
  };

  constructor(props) {
    super(props);

    this.present = new PresentingService();
  }

  componentDidMount() {
    const { history } = this.props;

    if (isUserSignedIn()) {
      if (history) {
        this.hideSplash();
        history.replace('/photos');
        return;
      }
    } else if (isSignInPending() && !isUserSignedIn()) {
      handlePendingSignIn().then(() => {
        if (history) {
          this.hideSplash();
          history.replace('/photos');
          AnalyticsService.logEvent('login', { method: 'Blockstack' });
        }
      });
      return;
    }
    
    this.setState({ loaded: true });

    this.hideSplash();
  }

  async hideSplash() {
    const { Device } = Plugins;

    const info = await Device.getInfo();
    if (info.platform !== 'web') {
      // Hide the splash (you should do this on app launch)
      setTimeout(() => {
        const { SplashScreen } = Plugins;
        SplashScreen.hide();
      }, 4000);
    }
  }

  async handleSignIn(e) {
    e.preventDefault();

    AnalyticsService.logEvent('login-started');

    const { Device } = Plugins;
    const info = await Device.getInfo();

    if (info.platform === 'android' || info.platform === 'ios' || isElectron()) {
      this.present.loading('Waiting for authentication...', 60000, true);
      let appDomain = 'https://app.block-photos.com';
      const transitPrivateKey = generateAndStoreTransitKey();
      const redirectURI = appDomain + '/redirect.html';
      const manifestURI = appDomain + '/manifest.json';
      const scopes = DEFAULT_SCOPE;
      let authRequest = makeAuthRequest(transitPrivateKey, redirectURI, manifestURI, scopes, appDomain);
      redirectToSignInWithAuthRequest(authRequest);
    } else {
      redirectToSignIn();
    }
  }

  render() {
    const { loaded } = this.state;
    return (
      <React.Fragment>
        <ion-header>
          <ion-toolbar mode="md" color="primary">
            <ion-title>Block Photos</ion-title>
          </ion-toolbar>
        </ion-header>
        <ion-content text-center class="signin">
          {!loaded ? (
            <ion-grid class="spinner-grid">
              <ion-row>
                <ion-col align-self-center>
                  <ion-spinner name="circles" />
                </ion-col>
              </ion-row>
            </ion-grid>
          ) : (
              <React.Fragment>
                <h1>Welcome to Block Photos!</h1>
                <ion-img padding src="data:image/png;base64," />

                <ion-card color="light">
                  <ion-card-content text-left>
                    <p>To get started click the login button below.</p>
                    <p>Blockstack will ask you to register an account if you  don&apos;t have one already.</p>
                    <p>Then all you need is to start adding photos with the upload button (<ion-icon size="small" name="ios-cloud-upload"></ion-icon>) or drag and drop them into the app view from your computers file browser.</p>
                  </ion-card-content>
                </ion-card>
                <ion-button padding-horizontal margin-bottom expand="block"
                  id="signin-button"
                  onClick={(event) => this.handleSignIn(event)}
                >
                  Sign In with Blockstack
            </ion-button>
              </React.Fragment>
            )}
        </ion-content>
      </React.Fragment>
    );
  }

}
